package com.example.juanmavela.listacolegios_juanma;

import android.content.Context;
import android.os.Bundle;
import android.support.v4.app.Fragment;
import android.support.v7.widget.GridLayoutManager;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.example.juanmavela.listacolegios_juanma.dummy.DummyContent;
import com.example.juanmavela.listacolegios_juanma.dummy.DummyContent.DummyItem;
import com.example.juanmavela.listacolegios_juanma.model.Colegio;
import com.example.juanmavela.listacolegios_juanma.model.ColegioInteracionListener;

import java.util.ArrayList;
import java.util.List;


public class colegioFragment extends Fragment {

    // TODO: Customize parameter argument names
    private static final String ARG_COLUMN_COUNT = "column-count";
    // TODO: Customize parameters
    private int mColumnCount = 1;
    private ColegioInteracionListener mListener;
    MycolegioRecyclerViewAdapter adapter;
    Context ctx;
    List<Colegio> listaColegios;



    public colegioFragment() {
    }


    public static colegioFragment newInstance(int columnCount) {
        colegioFragment fragment = new colegioFragment();
        Bundle args = new Bundle();
        args.putInt(ARG_COLUMN_COUNT, columnCount);
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        if (getArguments() != null) {
            mColumnCount = getArguments().getInt(ARG_COLUMN_COUNT);
        }
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_colegio_list, container, false);

        // Set the adapter
        if (view instanceof RecyclerView) {
            Context context = view.getContext();
            RecyclerView recyclerView = (RecyclerView) view;
            if (mColumnCount <= 1) {
                recyclerView.setLayoutManager(new LinearLayoutManager(context));
            } else {
                recyclerView.setLayoutManager(new GridLayoutManager(context, mColumnCount));
            }

            listaColegios = new ArrayList<>();
            listaColegios.add(new Colegio("Salesianos San Pedro","Condes de Bustillo,17 Sevilla","37.380368","-6.007181","Infantil,Primaria,Secundaria,Bachillerato y FP","http://www.artesacro.org/imagenes/La%20carcel.JPG"));
            listaColegios.add(new Colegio("Colegio  Alfares","Calle Juan Diaz de Solís,1 Sevilla","37.378210","-6.008120","Educación Primaria","data:image/jpeg;base64,"));
            listaColegios.add(new Colegio("Nuestra Sra del Rosario","Calle Pagés del Corro, 32, 41010 Sevilla","37.384850","-6.007650","Educación Primaria y Secundaria","http://sevillaciudad.sevilla.abc.es/wp-content/uploads/2013/06/escuela-rosario-triana-260x152.jpg"));
            listaColegios.add(new Colegio("Maristas San Fernando","Plaza Marcelino Champagnat, s/n, 41010 Sevilla","36.727550","-4.414560","Educación Infantil, Primaria, Secundaria y Bachillerato","data:image/jpeg;base64,"));
            listaColegios.add(new Colegio("Colegio  Rico Cejudo","Calle Coruña, 1, 41010 Sevilla","37.382070","-6.012180","Educación Infantil y Primaria","http://www.juntadeandalucia.es/averroes/centros-tic/41009408/helvia/sitio/upload/img/ricocejudo.png"));
            listaColegios.add(new Colegio("Centro Infantil Virgen de la Estrella","Plaza del Zurraque, 8, 41010 Sevilla","37.383620","-6.008210","Educación Infantil","http://www.juntadeandalucia.es/averroes/centros-tic/23003247/helvia/sitio/upload/img/colegio_1.jpg"));
            listaColegios.add(new Colegio("Centro Infantil Triana","Calle Espartinas, 6, 41010 Sevilla","37.381240","-6.007090","Educación Infantil","http://escuelainfantiltriana.com/wp-content/uploads/galeria-escuela/guarde_triana_01.jpg"));


            adapter = new MycolegioRecyclerViewAdapter(context,R.layout.fragment_colegio,listaColegios,mListener);
            recyclerView.setAdapter(adapter);

        }
        return view;
    }


    @Override
    public void onAttach(Context context) {
        ctx = context;
        super.onAttach(context);
        if (context instanceof ColegioInteracionListener) {
            mListener = (ColegioInteracionListener) context;
        } else {
            throw new RuntimeException(context.toString()
                    + " must implement ColegioInteracionListener");
        }
    }

    @Override
    public void onDetach() {
        super.onDetach();
        mListener = null;
    }



}
